import React, { useState } from 'react'
import NavBar from './NavBar'
import { useLoaderData, useNavigate } from 'react-router'
import TaskList from './TaskList';
import TaskModalForm from './TaskModalForm';

const Home = () => {
  const tasks = useLoaderData();
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);
  const [task, setTask] = useState({
    name: "",
    description: "",
  });

  return (
    <>
      <NavBar />
      <div className='flex justify-center'>
        <div className='flex flex-col justify-center w-96'>
          <div className='m-auto' style={{fontSize: "35px", fontWeight: "bold"}}>Tasks</div>
          <div className='flex justify-center'>
            <button className='my-2 rounded-md p-2 bg-blue-400' onClick={() => setIsOpen(true)}>Add Task</button>
          </div>
          <TaskList tasks={tasks} isOpen={isOpen} setIsOpen={setIsOpen} />
          <TaskModalForm isOpen={isOpen} setIsOpen={setIsOpen} t={undefined} />
        </div>
      </div>
    </>
  )
}

export default Home;
