import React, { useState } from 'react'
import NavBar from './NavBar'
import { useLoaderData, useNavigate } from 'react-router'
import TaskList from './TaskList';
import TaskModalForm from './TaskModalForm';
import ReactModal from 'react-modal';
import {upsert} from '../api/TaskService';

const Home = () => {
  const tasks = useLoaderData();
  const [isOpen, setIsOpen] = useState(false);
  const setCallback = () => setIsOpen(!isOpen);
  const navigate = useNavigate();
  const [task, setTask] = useState({
    id: t?.id ?? undefined,
    name: t?.name ?? undefined,
    description: t?.description ?? undefined,
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    upsert(task)
      .then(response => {
        setCallback();
        navigate("/");
      })
  }

  return (
    <>
      <NavBar />
      <div className='flex justify-center'>
        <div className='flex flex-col justify-center w-96'>
          <div className='m-auto' style={{fontSize: "35px", fontWeight: "bold"}}>Tasks</div>
          <div className='flex justify-center'>
            <button className='my-2 rounded-md p-2 bg-blue-400' onClick={() => setIsOpen(true)}>Add Task</button>
          </div>
          <TaskList tasks={tasks}/>
          <TaskModalForm isOpen={isOpen} setCallback={setCallback} t={undefined} />
        </div>
      </div>
      <ReactModal
      isOpen={isOpen}
      ariaHideApp={false}
    >
      <div className='flex justify-center h-full'>
        <form onSubmit={(e) => handleSubmit(e)} className='flex flex-col w-96'>
          <label className="block">
            <span className="after:content-['*'] after:ml-0.5 after:text-red-500 block text-sm font-medium text-slate-700">
              Name
            </span>
            <input required type="type" name="name" className="mt-1 px-3 py-2 bg-white border shadow-sm border-slate-300 placeholder-slate-400 focus:outline-none focus:border-sky-500 focus:ring-sky-500 block w-full rounded-md sm:text-sm focus:ring-1" 
              placeholder="Task Name" onChange={(e) => setTask({...task, name: e.target.value})} value={task.name}/>
          </label>
          <label className="block">
            <span className="block text-sm font-medium text-slate-700">
              Description
            </span>
            <textarea name="description" className="mt-1 px-3 py-2 bg-white border shadow-sm border-slate-300 placeholder-slate-400 focus:outline-none focus:border-sky-500 focus:ring-sky-500 block w-full rounded-md sm:text-sm focus:ring-1"
              onChange={(e) => setTask({...task, description: e.target.value})} value={task.description}/>
          </label>
          <div className='flex flex-row justify-center gap-1'>
            <button className='mt-2 p-1 bg-green-500  rounded-md' type="submit">Add</button>
            <button type='button' className='mt-2 p-1 bg-red-500  rounded-md' onClick={() => setCallback()}>Cancel</button>
          </div>
        </form>
      </div>
    </ReactModal>
    </>
  )
}

export default Home;
