import React, { useState } from 'react'
import NavBar from './NavBar'
import { useLoaderData } from 'react-router'
import ReactModal from 'react-modal';

const Home = () => {
  const tasks = useLoaderData();
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      <NavBar />
      <div className='flex justify-center'>
        <div className='flex flex-col justify-center'>
          <div className='m-auto' style={{fontSize: "35px", fontWeight: "bold"}}>Tasks</div>
          {
            tasks.map(task => 
              <div key={task.id} className='shadow-md' >
                <div>{task.name}</div>
                {task.description && <div>{task.description}</div>}
              </div>
            )
          }
          <button className='mt-2 rounded-full border-solid bg-blue-400' onClick={() => setIsOpen(true)}>Add Task</button>
          <ReactModal
            isOpen={isOpen}
          >
            <div className='h-full bg-sky-300'>
              <form action={""} className='flex flex-col'>
                <input aria-label='Name' type='text'/>
                <div>
                  <button className='bg-red-500 border-solid' onClick={() => setIsOpen(false)}>Cancel</button>
                </div>
              </form>
            </div>
          </ReactModal>
        </div>
      </div>
    </>
  )
}

export default Home;
