import React, { useState } from 'react'
import { useNavigate } from 'react-router'
import {deleteById, upsert} from '../api/TaskService';
import TaskModalForm from './TaskModalForm';

const TaskList = (props) => {
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);
  const [task, setTask] = useState({
    id: undefined,
    name: undefined,
    description: undefined,
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    upsert(task)
      .then(response => {
        setIsOpen(false);
        navigate("/");
      })
  }


  const handleEdit = (task) => {
    setIsOpen(true);
    setTask(task)
  }

  const handleDelete = (id) => {
    deleteById(id)
      .then(response => navigate("/"));
  }

  const taskFc = (task) =>
    <div key={task.id} className='p-3 shadow-md flex flex-row'>
      <div className='overflow-auto'>
        <div>{task.name}</div>
        {task.description !== "" && <div>{task.description}</div>}
      </div>
      <div className='ml-auto flex flex-col'>
        <button onClick={() => handleEdit(task)}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" dataSlot="icon" className="w-5 h-5">
            <path d="m5.433 13.917 1.262-3.155A4 4 0 0 1 7.58 9.42l6.92-6.918a2.121 2.121 0 0 1 3 3l-6.92 6.918c-.383.383-.84.685-1.343.886l-3.154 1.262a.5.5 0 0 1-.65-.65Z" />
            <path d="M3.5 5.75c0-.69.56-1.25 1.25-1.25H10A.75.75 0 0 0 10 3H4.75A2.75 2.75 0 0 0 2 5.75v9.5A2.75 2.75 0 0 0 4.75 18h9.5A2.75 2.75 0 0 0 17 15.25V10a.75.75 0 0 0-1.5 0v5.25c0 .69-.56 1.25-1.25 1.25h-9.5c-.69 0-1.25-.56-1.25-1.25v-9.5Z" />
          </svg>
        </button>
        <button onClick={() => handleDelete(task.id)}>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" dataSlot="icon" className="w-5 h-5">
            <path fillRule="evenodd" d="M8.75 1A2.75 2.75 0 0 0 6 3.75v.443c-.795.077-1.584.176-2.365.298a.75.75 0 1 0 .23 1.482l.149-.022.841 10.518A2.75 2.75 0 0 0 7.596 19h4.807a2.75 2.75 0 0 0 2.742-2.53l.841-10.52.149.023a.75.75 0 0 0 .23-1.482A41.03 41.03 0 0 0 14 4.193V3.75A2.75 2.75 0 0 0 11.25 1h-2.5ZM10 4c.84 0 1.673.025 2.5.075V3.75c0-.69-.56-1.25-1.25-1.25h-2.5c-.69 0-1.25.56-1.25 1.25v.325C8.327 4.025 9.16 4 10 4ZM8.58 7.72a.75.75 0 0 0-1.5.06l.3 7.5a.75.75 0 1 0 1.5-.06l-.3-7.5Zm4.34.06a.75.75 0 1 0-1.5-.06l-.3 7.5a.75.75 0 1 0 1.5.06l.3-7.5Z" clipRule="evenodd" />
          </svg>
        </button>
      </div>
    </div>


  return (
    <div>
      {
        props.tasks.map(task => taskFc(task))
      }

    </div>
  )
}

export default TaskList
