import React, { useState } from 'react'
import NavBar from './NavBar'
import { useLoaderData } from 'react-router'
import ReactModal from 'react-modal';

const Home = () => {
  const tasks = useLoaderData();
  const [isOpen, setIsOpen] = useState(false);

  const [cnt, setCnt] = useState(0);
  console.log(cnt);

  const handleClick = () => {
    for (let index = 0; index < 5; index++) {
      setCnt(cnt => cnt + 1);
    }
  }

  const handleClick1 = () => {
    setIsOpen(!isOpen);
  }

  return (
    <>
      <NavBar />
      <div className='flex justify-center'>
        <div className='flex flex-col justify-center'>
          <div className='m-auto' style={{fontSize: "35px", fontWeight: "bold"}}>Tasks</div>
          {
            tasks.map(task => 
              <div key={task.id} className='shadow-md' >
                <div>{task.name}</div>
                {task.description && <div>{task.description}</div>}
              </div>
            )
          }
          <button className='mt-2' onClick={() => setIsOpen(true)}>Add Task</button>
          <ReactModal
            isOpen={isOpen}
          >
            <div className='h flex flex-col bg-slate-500'>
              <form action={""}>
                <input aria-label='Name' type='text'/>
                <button className='bg-red-500' onClick={() => setIsOpen(false)}>Cancel</button>
              </form>
            </div>
          </ReactModal>
        </div>
      </div>
    </>
  )
}

export default Home;
